import React, { useContext, useState, useEffect } from "react";

import { Context } from "../context";

const ProductPage = ({ slug }) => {
  const products = useContext(Context).data;
  const [product, setProduct] = useState();

  useEffect(() => {
    if (products !== undefined) {
      setProduct(products.filter((product) => product.slug === slug));
      setProduct((product) => product[0]);
    }
  }, [products]);

  return (
    <React.Fragment>
      <header>
        <img
          src={`http://localhost:1337${product?.photo.formats.large.url}`}
          alt={product?.photo.alternativeText}
        />
        <div className="header-title-container">
          <h1>{product?.name}</h1>
          <div className="line" />
        </div>
      </header>

      <style jsx>{`
        img {
          width: 100%;
          height: 60vh;
          object-fit: cover;
          position: relative;
        }

        .header-title-container {
          position: absolute;
          top: 20%;
          left: 15%;
          height: 30%;
          width: 70%;
          background: rgba(0, 0, 0, 0.5);
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }
      `}</style>
    </React.Fragment>
  );
};

export default ProductPage;
