import React, { useContext } from "react";
import Link from "next/link";

import Layout from "../../components/Layout";
import { CartContext } from "../../context/CartContext";
import spinner from "../../utils/spinner";
import host from "../../utils/host";

const CartPage = () => {
  const cartContext = useContext(CartContext);

  const removeFromCart = (cartItem) => {
    if (cartContext) {
      cartContext.removeItem(cartItem);
    }
  };

  return (
    <React.Fragment>
      <Layout>
        {cartContext ? (
          cartContext.cartItems.map((cartItem) => (
            <div className="cart-item-container">
              <div className="left">
                <img
                  className="item-img"
                  src={`${
                    host === "http://localhost:1337"
                      ? "http://localhost:1337"
                      : ""
                  }${cartItem?.photo.formats.small.url}`}
                  alt={cartItem?.photo.alternativeText}
                />
              </div>
              <div className="right">
                <Link
                  href={`/products/${cartItem.categories[0].slug}/${cartItem.slug}`}
                >
                  <h1 className="product-name">{cartItem.name}</h1>
                </Link>
                <p className="product-description">{cartItem.description}</p>
                <button
                  className="remove-cart-btn"
                  onClick={() => removeFromCart(cartItem)}
                >
                  Remove from Cart
                </button>
              </div>
            </div>
          ))
        ) : (
          <img className="spinner" src={spinner} alt="spinner" />
        )}
      </Layout>

      <style jsx>{`
        .spinner {
          display: block;
          margin: 0 auto;
          width: 200px;
        }

        .cart-item-container {
          width: 85%;
          margin: 50px auto;
          border: 2px solid grey;
          display: flex;
          padding: 20px;
        }

        .left {
          width: 35%;
          margin-right: 20px;
        }

        .item-img {
          height: 100%;
          width: 100%;
          object-fit: cover;
        }

        .right {
          width: calc(100% - 35%);
          display: flex;
          flex-direction: column;
          justify-content: space-between;
        }

        .product-name {
          cursor: pointer;
        }

        .product-description {
          display: block;
          text-overflow: ellipsis;
          word-wrap: break-word;
          overflow: hidden;
          line-height: 1.4em;
          max-height: ${1.4 * 6}em;
        }

        .remove-cart-btn {
          cursor: pointer;
        }
      `}</style>
    </React.Fragment>
  );
};

export default CartPage;
