import React, { useContext, useState, useEffect } from "react";

import SectionTitle from "./SectionTitle";
import Product from "./Product";
import { Context } from "../context";
import spinner from "../spinner";

const Featured = () => {
  const products = useContext(Context).data;
  const [featured, setFeatured] = useState();

  useEffect(() => {
    if (products !== undefined) {
      setFeatured(products.filter((item) => item.featured === true));
    }
  }, [products]);

  return (
    <React.Fragment>
      <section>
        <SectionTitle titleText={"Featured"} />
        <div className="grid-container">
          {featured ? (
            featured.map((product) => {
              return (
                <Product
                  key={product.id}
                  product={product}
                  imgSrc={`${product.photo.formats.medium.url}`}
                  alternativeTxt={product.photo.alternativeText}
                  name={product.name}
                  price={product.price}
                  categorySlug={product.categories[0].slug}
                  productSlug={product.slug}
                />
              );
            })
          ) : (
            <img className="spinner" src={spinner} alt="Spinner" />
          )}
        </div>
      </section>

      <style jsx>{`
        section {
          background: white;
          padding: 1px 0;
        }

        .grid-container {
          margin-top: 2em;
          height: 350px;
          display: grid;
          grid-auto-flow: row;
          grid-template-columns: 1fr 1fr 1fr;
          border: 1px solid red;
        }

        .spinner {
          grid-column-start: 2;
        }

        @media screen and (max-width: 900px) {
          section {
            height: 840px;
          }

          .grid-container {
            ${featured ? { display: "block" } : { display: "grid" }}
            grid-template-columns: 1fr 1fr;
          }

          .spinner {
          }
        }

        @media screen and (max-width: 700px) {
          section {
            height: 1170px;
          }

          .grid-container {
            grid-template-columns: 1fr;
          }
        }
      `}</style>
    </React.Fragment>
  );
};

export default Featured;
