  //     setState({ ...state, cartItems: [...state.cartItems, cartItems] });
  //   }
  // }, []);
  //
  // useEffect(() => {
  //   if (state.cartItems) {
  //     localStorage.setItem("cartItems", state.cartItems);
  //   }
  // }, [state]);

  return (
    <CartContext.Provider value={state}>{props.children}</CartContext.Provider>
  );
};
