import React, { useContext, useState, useEffect } from "react";
import Link from "next/link";

import Layout from "../../components/Layout";
import { CartContext } from "../../context/CartContext";
import spinner from "../../utils/spinner";
import host from "../../utils/host";

const CartPage = () => {
  const [totalCost, setTotalCost] = useState(0);

  const cartContext = useContext(CartContext);

  const findAmount = (cartItem) => {
    return cartContext.cartItemsAmount[
      cartContext.cartItems.findIndex(
        (cartContextItem) => cartContextItem.name === cartItem.name
      )
    ];
  };

  useEffect(() => {
    if (cartContext !== undefined) {
      if (cartContext.cartItems.length > 0) {
        let accumulator = 0;
        cartContext.cartItems.forEach((item) => {
          accumulator += item.price;
        });
        setTotalCost(accumulator);
      } else {
        setTotalCost(0);
      }
    }
  }, [cartContext.cartItems, cartContext.cartItemsAmount]);

  const removeFromCart = (cartItem) => {
    if (cartContext) {
      cartContext.removeItem(cartItem);
    }
  };

  return (
    <React.Fragment>
      <Layout>
        <div className="container">
          {cartContext ? (
            cartContext.cartItems.map((cartItem) => (
              <div className="cart-item-container">
                <div className="left">
                  <img
                    className="item-img"
                    src={`${
                      host === "http://localhost:1337"
                        ? "http://localhost:1337"
                        : ""
                    }${cartItem?.photo.formats.small.url}`}
                    alt={cartItem?.photo.alternativeText}
                  />
                </div>
                <div className="right">
                  <div>
                    <Link
                      href={`/products/${cartItem.categories[0].slug}/${cartItem.slug}`}
                    >
                      <h1 className="product-name">{cartItem.name}</h1>
                    </Link>
                    <p className="product-description">
                      {cartItem.description}
                    </p>
                  </div>
                  <div className="price-remove-cart-btn">
                    <div className="product-amt">
                      <span
                        className="add"
                        onClick={() => cartContext.incrAmt(cartItem)}
                      >
                        +
                      </span>
                      <span className="amt">{findAmount(cartItem)}</span>
                      <span
                        className="subtract"
                        onClick={() => {
                          if (findAmount(cartItem) > 1) {
                            cartContext.decrAmt(cartItem);
                          }
                        }}
                      >
                        –
                      </span>
                    </div>
                    <p className="product-price">
                      ${cartItem.price * findAmount(cartItem)}
                    </p>
                    <button
                      className="remove-cart-btn"
                      onClick={() => removeFromCart(cartItem)}
                    >
                      Remove from Cart
                    </button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <img className="spinner" src={spinner} alt="spinner" />
          )}
          <div className="cart-info">
            <p>
              {cartContext?.cartItems.length === 0
                ? null
                : `Total: ${totalCost}`}
            </p>
          </div>
        </div>
      </Layout>

      <style jsx>{`
        .spinner {
          display: block;
          margin: 0 auto;
          width: 200px;
        }

        .container {
          width: 85%;
          margin: 0 auto;
        }

        .cart-item-container {
          width: 100%;
          margin: 50px 0;
          border: 2px solid #808080;
          display: flex;
          padding: 20px;
          height: auto;
        }

        .left {
          width: 35%;
          margin-right: 20px;
        }

        .item-img {
          height: 100%;
          width: 100%;
          object-fit: cover;
        }

        .right {
          width: calc(100% - 35%);
          display: flex;
          flex-direction: column;
          justify-content: space-between;
        }

        .product-name {
          cursor: pointer;
        }

        .product-description {
          display: block;
          word-wrap: break-word;
          overflow: hidden;
          line-height: 1.4em;
          max-height: ${1.4 * 6}em;
          margin: 10px 0;
        }

        .price-remove-cart-btn {
          display: flex;
          align-items: center;
          align-self: flex-end;
          justify-content: flex-end;
        }

        .product-price {
          font-size: 1.2rem;
        }

        .product-amt {
          border: 0.7px solid #b1b1b1;
          width: 60px;
          display: grid;
          grid-template-columns: 2.5fr 3fr 2.5fr;
          margin-right: 15px;
        }

        .product-amt span {
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .subtract,
        .add {
          cursor: pointer;
          background: #b1b1b1;
          color: #fff;
        }

        .remove-cart-btn {
          cursor: pointer;
          min-width: 100px;
          height: 25px;
          border: none;
          font-size: 0.6rem;
          margin: 10px 20px;
          background: #bf4141;
          color: white;
          border-radius: 6px;
        }

        .remove-cart-btn:hover {
          background: #ab0f0f;
        }

        .cart-info {
          float: right;
          transform: translateY(-20px);
          font-size: 1.3rem;
        }

        @media screen and (max-width: 600px) {
          .left,
          .right {
            width: 100%;
          }

          .right {
            margin-top: 20px;
          }

          .cart-item-container {
            flex-direction: column;
          }

          .price-remove-cart-btn {
            margin-top: 10px;
          }

          .product-description {
            max-height: ${1.4 * 7}em;
          }
        }
      `}</style>
    </React.Fragment>
  );
};

export default CartPage;
