import React, { useContext, useState, useEffect } from "react";
import Link from "next/link";

import { Context } from "../context";

const ProductPage = ({ slug }) => {
  const products = useContext(Context).data;
  const [product, setProduct] = useState();

  useEffect(() => {
    if (products !== undefined) {
      setProduct(products.filter((product) => product.slug === slug));
      setProduct((product) => product[0]);
    }
  }, [products]);

  return (
    <React.Fragment>
      <header>
        <img
          src={`http://localhost:1337${product?.photo.formats.large.url}`}
          alt={product?.photo.alternativeText}
        />
        <div className="header-title-container">
          <h1 className="title">{product?.name}</h1>
          <div className="line" />
          <Link href={`/products/${product?.categories[0].slug}`}>
            <button className="back">BACK</button>
          </Link>
        </div>
        <div className="add-to-cart">
          <button className="cart">ADD TO CART</button>
        </div>
      </header>

      <style jsx>{`
        img {
          width: 100%;
          height: 60vh;
          object-fit: cover;
          position: relative;
        }

        .header-title-container {
          position: absolute;
          top: 20%;
          left: 15%;
          height: 30%;
          width: 70%;
          background: rgba(0, 0, 0, 0.5);
          display: flex;
          flex-direction: column;
          justify-content: center;
        }

        .title {
          color: white;
          font-size: 50px;
          text-align: center;
          margin-bottom: 4px;
        }

        .line {
          width: 70px;
          padding-top: 5px;
          border-bottom: 4px solid #af9b7d;
          margin: 0 auto;
        }

        .back {
          width: 120px;
          height: 15%;
          margin: 0 auto;
          margin-top: 20px;
          background: #af9b7d;
          border: 3px solid #af9b7d;
          color: black;
          font-size: 1rem;
          cursor: pointer;
          z-index: 3;
          transition-property: all;
          transition-duration: 0.3s;
          transition-timing-function: ease-in;
        }

        .back:hover {
          background: none;
        }

        .add-to-cart {
          display: flex;
          justify-content: flex-end;
        }

        .add {
          margin-right: 40px;
        }
      `}</style>
    </React.Fragment>
  );
};

export default ProductPage;
