import React from "react";

const Product = ({ imgSrc, name, price }) => {
  function showOverlay(e) {
    e.target.parentNode.firstElementChild.style.opacity = "100%";
  }

  function hideOverlay(e) {
    e.target.parentNode.firstElementChild.style.opacity = "0";
  }

  return (
    <React.Fragment>
      <div className="grid-item">
        <div className="container">
          <div
            className="overlay"
            onMouseEnter={showOverlay}
            onMouseLeave={hideOverlay}
          />
          <button type={"click"} onMouseEnter={showOverlay}>
            LEARN MORE
          </button>
          <img src={imgSrc} alt={product.photo.alternativeText} />
        </div>
        <p className="name">{product.name}</p>
        <p className="price">${product.price}</p>
      </div>
    </React.Fragment>
  );
};

export default Product;
