import React, { useContext, useState, useEffect } from "react";
import Link from "next/link";

import { Context } from "../context";

const ProductPage = ({ slug }) => {
  const products = useContext(Context).data;
  const [product, setProduct] = useState();

  useEffect(() => {
    if (products !== undefined) {
      setProduct(products.filter((product) => product.slug === slug));
      setProduct((product) => product[0]);
    }
  }, [products]);

  return (
    <React.Fragment>
      <header>
        <img
          src={`${product?.photo.formats.large.url}`}
          alt={product?.photo.alternativeText}
        />
        <div className="header-title-container">
          <h1 className="title">{product?.name}</h1>
          <div className="line" />
          <Link href={`/products/${product?.categories[0].slug}`}>
            <button className="back">BACK</button>
          </Link>
        </div>
        <div className="main-body-section">
          <main>
            <h2 className="description-title">Description:</h2>
            <p className="description">{product?.description}</p>
          </main>
          <aside>
            <h2>Price: ${product?.price}</h2>
            <h4>
              <strong>Ingredients:</strong> {product?.ingredients}
            </h4>
          </aside>
        </div>
      </header>

      <style jsx>{`
        img {
          width: 100%;
          height: 450px;
          object-fit: cover;
          position: relative;
        }

        .header-title-container {
          position: absolute;
          //top: 25%;
          //left: 15%;
          top: 160px;
          left: 50%;
          margin-left: -500px;
          height: 25%;
          width: 70%;
          background: rgba(0, 0, 0, 0.5);
          display: flex;
          flex-direction: column;
          justify-content: center;
        }

        .title {
          color: white;
          font-size: 50px;
          text-align: center;
          margin-bottom: 4px;
        }

        .line {
          width: 70px;
          padding-top: 5px;
          border-bottom: 4px solid #af9b7d;
          margin: 0 auto;
        }

        .back {
          width: 120px;
          height: 15%;
          margin: 0 auto;
          margin-top: 20px;
          background: #af9b7d;
          border: 3px solid #af9b7d;
          color: black;
          font-size: 1rem;
          cursor: pointer;
          transition-property: all;
          transition-duration: 0.3s;
          transition-timing-function: ease-in;
        }

        .back:hover {
          background: none;
        }

        .main-body-section {
          width: 100%;
          display: flex;
          justify-content: space-evenly;
          margin: 10px;
        }

        main {
          width: 45%;
          margin-right: 10px;
        }

        aside {
          margin-right: 5px;
          width: 45%;
        }

        .description {
          margin-top: 10px;
        }

        h4 {
          margin-top: 10px;
          font-weight: 400;
        }

        strong {
          font-size: 22px;
          display: block;
        }

        @media screen and (max-width: 1020px) {
          .header-title-container {
            top: 23%;
          }
        }

        @media screen and (max-width: 900px) {
          .header-title-container {
            top: 20%;
          }
        }

        @media screen and (max-width: 700px) {
          .header-title-container {
            top: 17%;
            height: 22%;
          }

          .title {
            font-size: 42px;
          }

          .main-body-section {
            flex-direction: column-reverse;
            align-items: center;
          }

          main {
            width: 85%;
            margin-bottom: 5px;
          }

          aside {
            width: 85%;
            margin-bottom: 15px;
          }
        }

        @media screen and (max-width: 600px) {
          .header-title-container {
            top: 16%;
          }

          .title {
            font-size: 38px;
          }

          .back {
            font-size: 0.8rem;
          }
        }

        @media screen and (max-width: 500px) {
          .header-title-container {
            top: 16%;
            height: 17%;
          }

          .title {
            font-size: 32px;
          }

          .back {
            font-size: 0.7rem;
          }
        }
      `}</style>
    </React.Fragment>
  );
};

export default ProductPage;
