import React, { useContext, useState, useEffect } from "react";
import { useRouter } from "next/router";

import Layout from "../../../components/Layout";
import { CartContext } from "../../../context/CartContext";
import host from "../../../utils/host";
import SimpleInput from "../../../components/SimpleInput";

const CheckoutPage = () => {
  const [totalCost, setTotalCost] = useState(0);

  const cartContext = useContext(CartContext);

  const router = useRouter();

  const findAmount = (cartItem) => {
    return cartContext.cartItemsAmount[
      cartContext.cartItems.findIndex(
        (cartContextItem) => cartContextItem.name === cartItem.name
      )
    ];
  };

  useEffect(() => {
    if (cartContext !== undefined) {
      if (cartContext.cartItems.length > 0) {
        let accumulator = 0;
        cartContext.cartItems.forEach((item) => {
          accumulator += item.price * findAmount(item);
        });
        setTotalCost(accumulator);
      } else {
        setTotalCost(0);
      }
    }
  }, []);

  const placeOrder = (e) => {
    e.preventDefault();
    //
    // cartContext.resetCart();
    // setTotalCost(0);
  };

  return (
    <React.Fragment>
      <Layout>
        <div className="checkout-container">
          {cartContext &&
            cartContext.cartItems.map((cartItem) => (
              <div className="cart-item" key={cartItem.uuid}>
                <img
                  className="item-img"
                  src={`${
                    host === "http://localhost:1337"
                      ? "http://localhost:1337"
                      : ""
                  }${cartItem?.photo.formats.small.url}`}
                  alt={cartItem?.photo.alternativeText}
                />
                <div className="text">
                  <h1 className="product-name">{cartItem.name}</h1>
                  <p className="product-amt">Amount: {findAmount(cartItem)}</p>
                  <p className="product-cost">
                    Cost: ${cartItem.price * findAmount(cartItem)}
                  </p>
                </div>
              </div>
            ))}

          {totalCost > 0 && (
            <form className="cart-info" onSubmit={placeOrder}>
              <SimpleInput placeholder={"Name: "} name={"name"} />
              <SimpleInput
                placeholder={"Contact Email: "}
                name={"contactEmail"}
              />
              <div className="total-and-confirm">
                <h4 className="total">Total Cost: ${totalCost}</h4>
                <button className="confirm-place-order-btn" type={"submit"}>
                  Confirm Place Order
                </button>
              </div>
            </form>
          )}
        </div>
      </Layout>

      <style jsx>{`
        .checkout-container {
          width: 65%;
          margin: 70px auto;
          border: 0.7px solid #000;
          padding: 30px 40px;
        }

        img {
          max-width: 50%;
          width: auto;
        }

        .cart-item {
          display: flex;
        }

        .cart-item:not(:last-child) {
          margin-bottom: 40px;
        }

        .text {
          margin-left: 30px;
        }

        .text h1,
        .text p {
          margin-bottom: 10px;
        }

        .product-name {
          font-size: clamp(1.4rem, 2vw, 3rem);
          min-font-size: 1.5rem;
        }

        .cart-info {
          width: 100%;
          display: flex;
          flex-direction: column;
          align-items: flex-end;
        }

        .total-and-confirm {
          display: flex;
          align-items: center;
        }

        .confirm-place-order-btn {
          cursor: pointer;
          min-width: 130px;
          height: 25px;
          border: none;
          font-size: 0.6rem;
          margin: 10px 20px;
          border-radius: 6px;
          color: #fff;
          background: #15cb15;
        }

        .confirm-place-order-btn:hover {
          background: #16ae16;
        }

        @media screen and (max-width: 800px) {
          .checkout-container {
            width: 75%;
          }

          .cart-item {
            flex-direction: column;
          }

          img {
            max-width: 100%;
          }

          .text {
            margin-top: 30px;
            margin-left: 0;
          }
        }
      `}</style>
    </React.Fragment>
  );
};

export default CheckoutPage;
